mapper.select:
select
  id, order_id, goods_sku_id, quantity, price, discount, name, coupon_id, coupon_activity_id, be_share_id, gmt_create, gmt_modified
from order.order_item


        and id = #{id}


        and order_id = #{orderId}


        and goods_sku_id = #{goodsSkuId}


        and quantity = #{quantity}


        and price = #{price}


        and discount = #{discount}


        and name = #{name}


        and coupon_id = #{couponId}


        and coupon_activity_id = #{couponActivityId}


        and be_share_id = #{beShareId}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}
