mapper.select:
select
  id, amount, actual_amount, payment_pattern, pay_time, pay_sn, begin_time, end_time, order_id, state, gmt_create, gmt_modified, aftersale_id
from order.payment


        and id = #{id}


        and amount = #{amount}


        and actual_amount = #{actualAmount}


        and payment_pattern = #{paymentPattern}


        and pay_time = #{payTime}


        and pay_sn = #{paySn}


        and begin_time = #{beginTime}


        and end_time = #{endTime}


        and order_id = #{orderId}


        and state = #{state}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}


        and aftersale_id = #{aftersaleId}
