mapper.select:
select
  id, freight_model_id, first_items, first_items_price, additional_items, additional_items_price, region_id, gmt_create, gmt_modified
from order.piece_freight_model


        and id = #{id}


        and freight_model_id = #{freightModelId}


        and first_items = #{firstItems}


        and first_items_price = #{firstItemsPrice}


        and additional_items = #{additionalItems}


        and additional_items_price = #{additionalItemsPrice}


        and region_id = #{regionId}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}
